import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';

function App() {
  const [reviews, setReviews] = useState([]);
  const [groupedReviews, setGroupedReviews] = useState({});
  const [selectedShop, setSelectedShop] = useState('');

  useEffect(() => {
    // Загрузка данных с API и группировка по магазинам
    axios.get('http://localhost:8000/api/feedback/') // Замените URL на ваш DRF API
      .then(response => {
        setReviews(response.data);
        const grouped = {};
        response.data.forEach(review => {
          if (grouped[review.shop]) {
            grouped[review.shop].push(review);
          } else {
            grouped[review.shop] = [review];
          }
        });
        setGroupedReviews(grouped);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const toggleShopReviews = (shop) => {
    setSelectedShop(shop === selectedShop ? '' : shop);
  };

  const filteredReviews = selectedShop ? groupedReviews[selectedShop] : reviews;

  return (
    <div className='revs-container'>

      <div>
        <select className='dropdown revs-container' value={selectedShop} onChange={(e) => setSelectedShop(e.target.value)}>
          <option value=''>Все магазины</option>
          {Object.keys(groupedReviews).map(shop => (
            <option key={shop} value={shop}>
              {shop}
            </option>
          ))}
        </select>
      </div>
      <ul>
        {filteredReviews.map((review) => (
          <div className='all-reviews-container'>
            <div className="review-container">
              <span className="user-name">{review.user}</span>
              <span className="rating">Оценка: {review.rating}</span>
              <span className="review-text">{review.review}</span>
            </div>
          </div>
        ))}
      </ul>
    </div>
  );
}

export default App;
