import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';

function NewReview() {
  const [formData, setFormData] = useState({
    user: '',
    review: '',
    shop: '',
    rating: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Отправка данных на сервер (вашему API)
    axios.post('http://localhost:8000/api/feedback/', formData)
      .then((response) => {
        // Обработка успешной отправки, например, сброс формы
        setFormData({
          user: '',
          review: '',
          shop: '',
          rating: '',
        });
      })
      .catch((error) => {
        console.error('Ошибка при отправке отзыва', error);
      });
  };

  return (
    <div className="review-form">
      <h2>Оставьте свой отзыв</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Имя пользователя:</label>
          <input
            type="text"
            name="user"
            value={formData.user}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Отзыв:</label>
          <textarea
            name="review"
            value={formData.review}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Магазин:</label>
          <select
            name="shop"
            value={formData.shop}
            onChange={handleChange}
            required
          >
            <option value="">Выберите магазин</option>
            <option value="Дорорс">Дорорс</option>
            <option value="Розы.бел">Розы.бел</option>
            <option value="rozyminsk.by">rozyminsk.by</option>
            <option value="Розы.бел">Розы.бел</option>
            {/* Добавьте другие магазины как <option> элементы */}
          </select>
        </div>
        <div className="form-group">
          <label>Оценка:</label>
          <select
            name="rating"
            value={formData.rating}
            onChange={handleChange}
            required
          >
            <option value="">Выберите оценку</option>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
          </select>
        </div>
        <button className='btn btn-primary' type="submit">Отправить отзыв</button>
      </form>
    </div>
  );
}

export default NewReview;
