import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css'
function App() {
  const [reviews, setReviews] = useState([]);
  const [groupedReviews, setGroupedReviews] = useState({});
  const [selectedShop, setSelectedShop] = useState(null);

  useEffect(() => {
    // Загрузка данных с API и группировка по магазинам
    axios.get('http://localhost:8000/api/feedback/') // Замените URL на ваш DRF API
      .then(response => {
        setReviews(response.data);
        const grouped = {};
        response.data.forEach(review => {
          if (grouped[review.shop]) {
            grouped[review.shop].push(review);
          } else {
            grouped[review.shop] = [review];
          }
        });
        setGroupedReviews(grouped);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const toggleShopReviews = (shop) => {
    setSelectedShop(shop === selectedShop ? null : shop);
  };

  return (
    <div className="app">
      <h1>Отзывы на магазины</h1>
      {Object.keys(groupedReviews).map(shop => (

        <div key={shop}>
          <button className = 'btn btn-light' onClick={() => toggleShopReviews(shop)}>
            Отзывы на магазин "{shop}"
          </button>

          {selectedShop === shop && (
            <ul>
              <div className='feedback'>
              {groupedReviews[shop].map((review) => (
                <div className='all-reviews-container'>
                <div className="review-container">
                <span className="user-name">{review.user}</span>
                <span className="rating">Рейтинг: {review.rating}</span>
                <span className="review-text">{review.review}</span>
              </div>
              </div>



              ))}
              </div>
            </ul>
          )}
        </div>
      ))}
    </div>
  );
}

export default App;
