import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [reviews, setReviews] = useState([]);
  const [groupedReviews, setGroupedReviews] = useState({});
  const [selectedShop, setSelectedShop] = useState(null);

  useEffect(() => {
    // Загрузка данных с API и группировка по магазинам
    axios.get('http://localhost:8000/api/feedback/') // Замените URL на ваш DRF API
      .then(response => {
        setReviews(response.data);
        const grouped = {};
        response.data.forEach(review => {
          if (grouped[review.shop]) {
            grouped[review.shop].push(review);
          } else {
            grouped[review.shop] = [review];
          }
        });
        setGroupedReviews(grouped);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const toggleShopReviews = (shop) => {
    setSelectedShop(shop === selectedShop ? null : shop);
  };

  return (
    <div className="app">
      <h1>Отзывы по магазинам</h1>
      {Object.keys(groupedReviews).map(shop => (
        <div key={shop}>
          <button onClick={() => toggleShopReviews(shop)}>
            Отзывы на магазин "{shop}"
          </button>
          {selectedShop === shop && (
            <ul>
              {groupedReviews[shop].map((review, index) => (
                <li key={index}>
                  {`Отзыв: ${review.review}, Пользователь: ${review.user}, Рейтинг: ${review.rating}`}
                </li>
              ))}
            </ul>
          )}
        </div>
      ))}
    </div>
  );
}

export default App;
