import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css'

function App() {
  const [reviews, setReviews] = useState([]);
  const [groupedReviews, setGroupedReviews] = useState({});
  const [selectedShop, setSelectedShop] = useState(null);
  const [showAllShops, setShowAllShops] = useState(true);

  useEffect(() => {
    // Загрузка данных с API и группировка по магазинам
    axios.get('http://localhost:8000/api/feedback/') // Замените URL на ваш DRF API
      .then(response => {
        setReviews(response.data);
        const grouped = {};
        response.data.forEach(review => {
          if (grouped[review.shop]) {
            grouped[review.shop].push(review);
          } else {
            grouped[review.shop] = [review];
          }
        });
        setGroupedReviews(grouped);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const toggleShopReviews = (shop) => {
    if (shop === selectedShop) {
      setSelectedShop(null);
      setShowAllShops(true); // При выборе "Все магазины" снова отображаем все отзывы
    } else {
      setSelectedShop(shop);
      setShowAllShops(false); // При выборе конкретного магазина скрываем отзывы для всех магазинов
    }
  };

  const filteredReviews = showAllShops ? reviews : groupedReviews[selectedShop] || [];

  return (
    <div className='container'>
      <h1>Отзывы на магазины</h1>
      {Object.keys(groupedReviews).map(shop => (
        <div key={shop}>
          <button className='btn btn-light' onClick={() => toggleShopReviews(shop)}>
            Отзывы на магазин "{shop}"
          </button>
          {selectedShop === shop && (
            <ul>
              {filteredReviews.map((review) => (
                <div className='all-reviews-container'>
                  <div className="review-container">
                    <span className="user-name">{review.user}</span>
                    <span className="rating">Оценка: {review.rating}</span>
                    <span className="review-text">{review.review}</span>
                  </div>
                </div>
              ))}
            </ul>
          )}
        </div>
      ))}
    </div>
  );
}

export default App;
