import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'


function ReviewList() {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8000/api/feedback/') // Замените URL на ваш DRF API
      .then(response => {
        setReviews(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (

<body className="body-bg">
    <div class="header">
        <div className="header-font">

        <b>Maps</b> and roses

    </div>
    </div>
    <div class="mycontainer"></div>
    <div>
      <h1 className='div-style'>Отзывы пользователей</h1>
      <ul>
        {reviews.map(review => (
        <div>
          {review.user}
          <div>
            {review.review}
          </div>
          <div>
            {review.shop}
          </div>
          <div>
            {review.rating}
          </div>
          <hr></hr>
          </div>
        ))}
      </ul>
    </div>
    </body>
  );
}

export default ReviewList;
