import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';

function NewReview() {
  const [review, setReview] = useState('');
  const [shop, setShop] = useState('');
  const [user, setUser] = useState('');
  const [rating, setRating] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Отправка данных на сервер или другая логика обработки формы
    console.log('Отправлен отзыв:', { review, shop, user, rating });
    axios.post('http://localhost:8000/api/feedback/')
};

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="review">Отзыв:</label>
        <textarea id="review" value={review} onChange={(e) => setReview(e.target.value)} required />
      </div>
      <div>
        <label htmlFor="shop">Магазин:</label>
        <input type="text" id="shop" value={shop} onChange={(e) => setShop(e.target.value)} required />
      </div>
      <div>
        <label htmlFor="user">Пользователь:</label>
        <input type="text" id="user" value={user} onChange={(e) => setUser(e.target.value)} required />
      </div>
      <div>
        <label htmlFor="rating">Рейтинг:</label>
        <select id="rating" value={rating} onChange={(e) => setRating(e.target.value)} required>
          <option value="">Выберите рейтинг</option>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
          <option value="4">4</option>
          <option value="5">5</option>
        </select>
      </div>
      <button type="submit">Отправить отзыв</button>
    </form>
  );
}

export default NewReview;
