import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ReviewList() {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8000/api/feedback/') // Замените URL на ваш DRF API
      .then(response => {
        setReviews(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const divStyle ={
    backgroundColor:'grey'
  }
  return (
    <div>
      <h1>Отзывы пользователей</h1>
      <ul>
        {reviews.map(review => (
        <div style={divStyle}>
          {review.user}
          <div>
            {review.review}
          </div>
          <div>
            {review.shop}
          </div>
          <div>
            {review.rating}
          </div>
          <hr></hr>
          </div>
        ))}
      </ul>
    </div>
  );
}

export default ReviewList;
