import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';

function ReviewForm({ onReviewSubmitted }) {
  // ... (остальной код)

  const handleSubmit = (e) => {
    e.preventDefault();
    // Отправка данных на сервер (вашему API)
    axios.post('http://localhost:8000/api/feedback/', formData)
      .then((response) => {
        // Обработка успешной отправки, например, сброс формы
        setFormData({
          user: '',
          review: '',
          shop: '',
          rating: '',
        });
        // Вызываем функцию обратного вызова для сообщения об успешной отправке
        onReviewSubmitted();
      })
      .catch((error) => {
        console.error('Ошибка при отправке отзыва', error);
      });
  }

  return (
    <div className="review-form">
      <h2>Оставьте свой отзыв</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            placeholder='Имя'
            type="text"
            name="user"
            value={formData.user}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <select className='rate-shop-dropdown'
            name="shop"
            value={formData.shop}
            onChange={handleChange}
            required
          >
            <option value="">Выберите магазин</option>
            <option value="Дорорс">Дорорс</option>
            <option value="Розы.бел">Розы.бел</option>
            <option value="rozyminsk.by">rozyminsk.by</option>
            <option value="Розы.бел">Розы.бел</option>
            <option value="daflor.by">daflor.by</option>
            <option value="яцветы.бел">яцветы.бел</option>
          </select>
        </div>
        <div className="form-group">
          <select
            className='rate-shop-dropdown'
            name="rating"
            value={formData.rating}
            onChange={handleChange}
            required
          >
            <option value="">Выберите оценку</option>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
          </select>
          <div className="form-group">
          <textarea className='review-textarea'
            placeholder='Отзыв'
            name="review"
            value={formData.review}
            onChange={handleChange}
            required
          />
        </div>
        </div>
        <button className='btn-form' type="submit">Отправить</button>
      </form>
    </div>
  );
}

export default ReviewForm;
